import React from 'react';
import { useParams } from 'react-router';

export default function CartScreen(props) {
    const params = useParams();
    const productId = params.id;
    const {search} =useLocation();
    const totalInUrl = new URLSearchParams(search).get('total');
    const total = totalInUrl?Number(totalInUrl):1;

return (
    <div>
      <h1>Cart Screen</h1>
      <p>
        ADD TO CART : ProductID: {productId} total: {total}
      </p>
    </div>
  );
}
